import { createGlobalStyle } from "styled-components";

export const GlobalStyle = createGlobalStyle`
    // this is the shared style
  * {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
  }


  body {
      color : blue;
  }
`;

const BasicLayout = ({ children }) => {
  return (
    <>
      {/* <GlobalStyle /> */}
      {children}
    </>
  );
};

export default BasicLayout;
