import { useRef, useState } from "react";

import {
  DivInputUpload,
  DivInputUploadTitle,
  DivInputUploadFile,
  CardUpload,
} from "./styles";

const InputUpload = () => {
  const inputRef = useRef(null);
  const [file, setFile] = useState(null);

  function onClickUpload() {
    let input = inputRef.current;
    input.click();
  }

  function onChangeInput(e) {
    console.log(e);
  }

  console.log(file);

  return (
    <>
      <DivInputUpload>
        <DivInputUploadTitle>
          <h2 className="title">Bulk Upload form </h2>
          <div className="text-help">
            <span>you haven't upload any bulk data yed</span>
          </div>
        </DivInputUploadTitle>
        <hr />
        <DivInputUploadFile>
          <h2 className="title">Choose your an input medthod</h2>
          <CardUpload>
            <div className="icon-upload" onClick={() => onClickUpload()}>
              <input
                ref={inputRef}
                type="file"
                name="fileToUpload"
                id="fileToUpload"
                size="1"
                style={{ display: "none" }}
                onChange={() => onChangeInput}
              />
              <i className="fas fa-upload" />
            </div>
            <div>
              <h4>via CSV file</h4>
              <h4>อัปเดตข้อมูลจากไฟล์ CSV </h4>
            </div>
          </CardUpload>
        </DivInputUploadFile>
      </DivInputUpload>
    </>
  );
};

export default InputUpload;
