import { useRef, useState } from "react";
import CSVReader from "react-csv-reader";

import {
  DivInputUpload,
  DivInputUploadTitle,
  DivInputUploadFile,
  CardUpload,
} from "./styles";

const InputUpload = () => {
  const inputRef = useRef(null);
  const [file, setFile] = useState(null);

  function onClickUpload() {
    let input2 = document.getElementById("inputFile");
    input2.click();
  }

  function onFileLoaded(data, fileInfo) {
    setFile(fileInfo);
    console.log(data, fileInfo);
  }

  console.log(file);

  return (
    <>
      <CSVReader
        inputStyle={{ display: "none" }}
        inputId={"inputFile"}
        onFileLoaded={(data, fileInfo) => onFileLoaded(data, fileInfo)}
      />
      <DivInputUpload>
        <DivInputUploadTitle>
          <h2 className="title">Bulk Upload form </h2>
          <div className="text-help">
            <span>you haven't upload any bulk data yed</span>
          </div>
        </DivInputUploadTitle>
        <hr />
        <DivInputUploadFile>
          <h2 className="title">Choose your an input medthod</h2>
          <CardUpload>
            <div className="icon-upload" onClick={() => onClickUpload()}>
              <i className="fas fa-upload" />
            </div>
            <div>
              <h4>{file ? file.name : "via CSV file "}</h4>
              <h4>via CSV file</h4>
              <h4>อัปเดตข้อมูลจากไฟล์ CSV</h4>
            </div>
          </CardUpload>
        </DivInputUploadFile>
      </DivInputUpload>
    </>
  );
};

export default InputUpload;
